mapper.update:
UPDATE t_feature
SET
sweety = #{sweety},
acidity = #{acidity},
body = #{body}
WHERE feature_id = #{featureId}

mapper.select:
SELECT category_id categoryId, feature_id featureId, country_id countryId, aroma_id aromaId
FROM t_product
WHERE product_id = #{productId}

SELECT product_id productId, nm_kor nmKor, price, promotion, beginner, quantity
FROM t_product
LIMIT #{startIdx}, #{row}

SELECT user_id userId, email, nm, region_nm_id regionNmId
FROM t_user
LIMIT #{startIdx}, #{row}

SELECT refund_id refundId, order_id orderId, refund_reason refundReason, refund_yn refundYn, refund_date refundDate
FROM t_order_refund
LIMIT #{startIdx}, #{row}

SELECT store_id storeId, region_nm_id regionNmId, nm, tel
FROM t_store
LIMIT #{startIdx}, #{row}

mapper.update:
UPDATE t_order
SET
order_status = #{orderStatus}
WHERE order_id = #{orderId}

UPDATE t_sale
SET
sale_yn = #{saleYn}

WHERE product_id = #{productId}
