mapper.update:
update t_user
set
nm=#{name},
tel=#{tel},
region_nm_id=#{regionNmId}
where user_id=#{userId}

update t_user
set pw=#{pw}
where user_id=#{userId}

mapper.select:
select user_id userId,email,pw,nm,tel,region_nm_id regionNmId,del_yn delYn
from t_user
where user_id=#{userId}

mapper.update:
update t_user
SET  del_yn = 1
where user_id=#{userId}
